const openBurger=document.querySelector(".navbar__burger"),closeBurger=document.querySelector(".navbar__cross"),menu=document.querySelector(".navbar__menu");function menuToggle(e){e.target.closest(".navbar__burger")?(openBurger.style.display="none",closeBurger.style.display="block",menu.style.display="block"):e.target===closeBurger&&(openBurger.style.display="block",closeBurger.style.display="none",menu.style.display="none")}openBurger.addEventListener("click",menuToggle),closeBurger.addEventListener("click",menuToggle);