const openBurger=document.querySelector(".navbar__burger"),closeBurger=document.querySelector(".navbar__cross"),menu=document.querySelector(".navbar__menu");function menuToggle(e){e.target.closest(".navbar__burger")?(openBurger.style.display="none",closeBurger.style.display="block",menu.classList.add("menu__adaptive")):e.target===closeBurger&&(openBurger.style.display="block",closeBurger.style.display="none",menu.classList.remove("menu__adaptive"))}openBurger.addEventListener("click",menuToggle),closeBurger.addEventListener("click",menuToggle);